import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule, Routes } from '@angular/router';
import { ProductsComponent } from './products/products.component';

const routes: Routes = [
  { path: '', pathMatch: 'full', redirectTo: 'products' },
  { path: 'products', component: ProductsComponent }
];

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterModule.forRoot(routes), ProductsComponent],
  template: `
  <nav class="navbar navbar-expand navbar-dark bg-dark mb-3">
    <a class="navbar-brand ms-2" routerLink="/">Sharp Shop</a>
    <ul class="navbar-nav ms-3">
      <li class="nav-item"><a class="nav-link" routerLink="/products" routerLinkActive="active">Products</a></li>
    </ul>
  </nav>
  <div class="container">
    <router-outlet></router-outlet>
  </div>
  `
})
export class AppComponent { }
